<script>
     export default {
          data() {
                    return {
                        fields: {},
                        laravelErrors: {},
                        location: '',
                        verification_type: ''                     
                    }
                },
            mounted()
            {            
                $('#location').on('mousedown',function()
                {
                    $("option[value='']").hide();
                });
                $('#verification_type').on('mousedown',function()
                {
                    $("option[value='']").hide();
                });
               $('#password').on('keyup keydown', function(e) 
                {
                    var phone = $(this).val();
                    var re = /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=]).{8,20}$/;
                    var oneNumber = /^(?=.*\d)/;
                    var oneSmallLetter = /^(?=.*\d)(?=.*[a-z])$/;
                    var oneCapLetter = /^(?=.*[A-Z])$/;
                    var oneSpecial = /^(?=.*[@#$%^&+=])$/;
                    var isValid = 1;
                    if(/[0-9]/.test(phone))
                    {
                        $('#oneNumber').css('color','green');
                    }
                    else
                    {
                        $('#oneNumber').css('color','red');
                        isValid = 0;
                    }
                    if(/[a-z]/.test(phone))
                    {
                        $('#oneSmallLetter').css('color','green');         
                    }
                    else
                    {
                        $('#oneSmallLetter').css('color','red');      
                        isValid = 0;  
                    }
                    if(/[A-Z]/.test(phone))
                    {
                        $('#oneCapLetter').css('color','green');         
                    }
                    else
                    {
                        $('#oneCapLetter').css('color','red');  
                        isValid =0;      
                    }
                    if(/[@#$%^&+=!()]/.test(phone))
                    {
                        $('#oneSpecial').css('color','green');         
                    }
                    else
                    {
                        $('#oneSpecial').css('color','red'); 
                        isValid= 0;       
                    }
                    if(phone.length < 8 || phone.length > 20)
                    {
                        $('#phoneLength').css('color','red');
                        isValid = 0;
                    }
                    else
                    {
                        $('#phoneLength').css('color','green');
                    }
                    if(isValid == 0)
                    {
                        $('#btnSubmit').attr('disabled',true);
                    }
                    else
                    {
                        $('#btnSubmit').attr('disabled',false); 
                    }
                        
                });

                $('#mobile').keyup(function()
                {
                    var phone  = $(this).val(); 
                    if(phone != '')
                    {                   
                        $('#verification_type').val('');
                        if(phone.charAt(0) == '2' || phone.charAt(0) == '9')
                        {
                            if(phone.charAt(0) == '2')
                            {
                                $('#otpV').prop('disabled', true)
                            }
                            else
                            {
                                $('#otpV').prop('disabled', false);
                            }
                            $(this).next().text('');
                            $('#btnSubmit').attr('disabled',false);
                        }
                        else
                        {
                            if($('#vue_mobile_error').text().length  == '')
                            {
                                $('#phoneerror').text('Phone number is not valid!');
                            }
                            else
                            {
                                 $('#phoneerror').text('');
                            }                            
                            //$(this).next().next().text('');
                            $('#btnSubmit').attr('disabled',true);
                        }
                    }
                    else
                    {
                         $('#phoneerror').text('');
                    }
                });
                 $('#imgConfirm').click(function()
                {
                    var dataId = $(this).attr('data-id');
                    if(dataId == 1)
                    {
                         var url = "/img/ic_show_24_dark.png";
                        $(this).attr('src',url);
                        $(this).attr('data-id',2);
                        $('#password').attr('type','text');
                    }
                    else
                    {
                         var url = "/img/ic_hide_24_dark.png";
                        
                        $(this).attr('src',url);
                        $(this).attr('data-id',1);
                        $('#password').attr('type','password');
                    }
                });
                jQuery("#cv_file").on('change',function (e) {           
                var labelVal = jQuery(".u-title").text();
                var oldfileName = jQuery(this).val();
                var fileName = e.target.value.split( '\\' ).pop();

                    if (oldfileName == fileName) {return false;}
                    var extension = fileName.split('.').pop();
                    var size = this.files[0].size;              
                if ((extension == 'pdf' || extension == 'doc' || extension == 'docx') && size <= 5242880) 
                { 
                    jQuery('#fileMsg').text('');
                    jQuery("#btnSubmit").attr('disabled',false);
                    jQuery(".check-icon").show();
                }
                else
                {
                    jQuery('#fileMsg').text('Only files with the following extensions are allowed: .doc, .docx, .pdf under 5 MB');
                    jQuery("#btnSubmit").attr('disabled',true);
                    jQuery(".check-icon").hide();
                }

                if (jQuery.inArray(extension,['jpg','jpeg','png']) >= 0) {
                    jQuery(".upload-icon i").removeClass().addClass('fa fa-file-image-o');
                
                }
                else if(extension == 'pdf'){
                    jQuery(".upload-icon i").removeClass().addClass('fa fa-file-pdf-o');
                    

                }
                else if(extension == 'doc' || extension == 'docx'){
                    jQuery(".upload-icon i").removeClass().addClass('fa fa-file-word-o');
                
                    jQuery(".upload-icon").css({'border':' 2px solid #2388df'});
                }
                else{
                    jQuery(".upload-icon i").removeClass().addClass('fa fa-file-o');               
                }

                if(fileName )
                {
                    if (fileName.length > 20){
                        jQuery(".filelabel .u-title").text(fileName.slice(0,4)+'...'+extension);
                    }
                    else{
                        jQuery(".filelabel .u-title").text(fileName);
                    }
                }
                else{
                    jQuery(".filelabel .u-title").text(labelVal);
                }
            });
            },
             methods: {
                        submit() {
                        $('.btnLoading').show();
                        this.laravelErrors = {};
                        let formData = new FormData(this.$refs.frm_candidate);
                        formData.append('cv_file', this.cv_file);
                        axios.post('/api/front/candidateRegister', formData).then(response => {
                             $('.btnLoading').hide();
                            if(response.data.status == 200)
                            {
                                $('#frm_candidate')[0].reset();
                                this.flashMessage.success({                                   
                                        message: response.data.message
                                    }); 
                                if(response.data.verification_type == 2)
                                {
                                    this.$router.push("/otpverify");
                                } 
                            }
                            else if(response.data.status == 501)
                            {
                                 this.flashMessage.error({
                                     message: response.data.message                          
                                });
                            }                      
                        }).catch(error => {
                             $('.btnLoading').hide();
                            if (error.response.status === 422) 
                            {
                                this.laravelErrors = error.response.data.errors || {};
                            }
                            else if(error.response.status === 500)
                            {
                                  this.flashMessage.error({
                                     message: 'Oops! Registration not done'                      
                                });
                            }
                        });
                        },
                        loginRedirect()
                        {
                            this.$router.push('/')
                        }
                    },
           
    }
</script>